import React, { useContext, useState, useEffect } from "react";
import {
  Text,
  View,
  StyleSheet,
  TouchableOpacity,
  Image,
  Linking,
  FlatList,
  ScrollView,
} from "react-native";
import Spacer from "../components/Spacer";
import { LinearGradient } from "expo-linear-gradient";
import ReelFeedCard from "../components/ReelFeedCard";
import { firebase } from "../firebase/config.js";
import { navigate } from "../navigationRef";

const ProfileCard = ({
  bio,
  name,
  uid,
  imageURL
}) => {
    const [profilePic, setProfilePic] = useState({uri: ""});
    useEffect(() => {
        getProfilePic(setProfilePic, uid, imageURL);
        return () => {
          null;
        };
      }, []);
  return (
    <TouchableOpacity
    onPress={(event) => navigate("ProfileView", { uid: uid})}
      style={{

      }}
    >
        <LinearGradient
          colors={["#DFD7FF", "#7D5BFC"]}
          style={{
            // height: 212.5,
            width: "100%",
            height: "100%",
            flex: 1,
            flexDirection: "row",
            zIndex: 0,
            aspectRatio: 16 / 9,
            // backgroundColor: "#DFD7FF",
            alignItems: "center",
            borderRadius: 5,
            // borderRadius: 25,
            // borderTopRightRadius: 0,
            // borderTopLeftRadius: 0,
          }}
        >
        <Image
            style={{
              marginLeft: 20,
              width: 100,
              height: 100,
              borderRadius: 100,
              zIndex: 1,
              borderColor: "white",
              borderWidth: 5,
            }}
            source={profilePic}
        />
        <View style={{
            flexDirection: "column",
            marginLeft: 20
        }}>
        <Text>Name: {name}</Text>
        <Text>Bio: {bio}</Text>
        </View>
        </LinearGradient>
    </TouchableOpacity>
  );
};

const renderReelFeedView = (data) => {
  return (
    <ReelFeedCard
      title={data.title}
      upvotes={data.upvotes}
      image_url={data.thumbnail}
      id={data.id}
      data={data}
    />
  );
};

const getProfilePic = async(setProfilePic, uid, imageURL) => {
    var storageRef = firebase.storage().ref();
    console.log("getting profilePic")
    if(imageURL == false) {
        return;
    }
    storageRef
    .child("profile_pictures/" + uid + ".jpg")
    .getDownloadURL()
    .then((url) => {
        console.log("url is " + url);
        setProfilePic({ uri: url });
    });
}

const styles = StyleSheet.create({
  topBar: {
    position: "absolute",
    top: 0,
    paddingTop: 45,
    backgroundColor: "rgba(0,0,0,0)",
    left: 0,
    width: "100%",
    flexDirection: "row",
    justifyContent: "space-between",
  },
  textInput: {
    color: "black",
  },
  imageCenter: {
    justifyContent: "center",
    alignItems: "center",
  },
  inline: {
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "space-around",
    // alignItems: "center",
    width: "50%",
    // borderWidth: 10,
  },
  name: {
    fontFamily: "Raleway-Bold",
    fontSize: 32,
  },
  bio: {
    fontFamily: "Raleway-Regular",
    fontSize: 16,
    color: "#545454",
  },
  reels: {
    fontFamily: "Raleway-Regular",
    fontSize: 14,
    textAlign: "left",
    width: "90%",
  },
  icon: {
    height: 20,
    width: 20,
    marginLeft: 10
  },
  backContainer: {
    padding: 8,
    marginLeft: 8,
  },
  uploadText: {
    color: "#5C33FF",
    fontFamily: "Raleway-Bold",
    fontSize: 18,
  },
});

export default ProfileCard;
